#include "gtkDialog.h"

Dialog::Dialog() {}

Dialog::~Dialog() {}

void Dialog::messageBox(int type, const std::string &msg) {
	GtkMessageType dlgType;
	switch(type) {
		case INFO:
			dlgType = GTK_MESSAGE_INFO;
			break;
		case WARNING:
			dlgType = GTK_MESSAGE_WARNING;
			break;
		case QUESTION:
			dlgType = GTK_MESSAGE_QUESTION;
			break;
		case ERROR:
			dlgType = GTK_MESSAGE_ERROR;
			break;
		default:
			dlgType = GTK_MESSAGE_OTHER;
			break;
	}
	GtkWidget *dlg = gtk_message_dialog_new(NULL, GTK_DIALOG_MODAL, dlgType
			, GTK_BUTTONS_OK, "%s", msg.c_str());

	gtk_dialog_run(GTK_DIALOG(dlg));
	gtk_widget_destroy(dlg);
}

void Dialog::propertyBox(const std::string &data) {}
